import axios from 'axios'
import { useState } from "react";
import WeatherCard from '../weathercard';
import { Button, Form } from 'react-bootstrap';
im
 
let Home = () => {

    const [cityName, setCityName] = useState("");
     const [data, setData] = useState([]);
    let submitHandler = async (e) => {
        e.preventDefault(); 

        console.log("I am submit handler function");

        try {
            let response = await axios.get(` https://pro.openweathermap.org/data/2.5/forecast/hourly?${cityName}&lat={lat}&lon={lon}&appid={9030900440d65b7988587a3e425672a9}&units=metric`)

            console.log("response: ", response);

            setData(response.data.list)


        } catch (error) {
            console.log("error in api call: ", error);
        }

    }


    return (
        <div >
            <h1>Weather App Home</h1>

            <form onSubmit={submitHandler}>

<Form.Group className="mb-3" controlId="exampleForm.ControlInput1">
    <Form.Label>City Name:</Form.Label>
    <Form.Control
        type="text"
        required placeholder='enter your city name'
        onChange={(e) => {
            // console.log(e.target.value);
            setCityName(e.target.value);
        }}
    />
 

                       <Button type='submit'>Get Weather</Button>
                </Form.Group>

              
            </form>



                {
                data.map((eachForcast, index) => (

                    <WeatherCard
                        key={index}
                        date={eachForcast.dt_txt}
                        temp={eachForcast.main.temp}
                        min={eachForcast.main.temp_min}
                        max={eachForcast.main.temp_max}
                    
                   
                    />
                ))
            }



        </div>
    );
}

export default Home;
